import { Component, Input, OnChanges, OnInit } from '@angular/core';

@Component({
  selector: 'input-calix',
  templateUrl: './input.component.html',
  styleUrls: ['./input.component.scss']
})
export class InputComponent implements OnInit, OnChanges {
  @Input('hint')  hintName = '';
  @Input('label')  label = null;
  @Input('error_message') error_message="null";
  @Input() type!:string;

  value = 'Hello';
  ngOnInit() {
    this.checkRequiredFields(this.label);
 }

 ngOnChanges(changes:any) {
    this.checkRequiredFields(this.label);
 }

 checkRequiredFields(input:any) {
    if(input === null) {
       throw new Error("Attribute 'label' is required");
    }
 }
  changeFn(e:any) {
    this.value = e.target.value;
  }
  modelChangeFn(value:any) {
    this.value = value;
  }

  addPrefix(){
    if (!this.value.startsWith('calix-')){
      this.value="calix-"+this.value

    }
  }


}
