import { Component } from '@angular/core';

@Component({
  selector: 'app-input',
  templateUrl: './input.component.html',
  styleUrls: ['./input.component.scss']
})
export class InputComponent {
  value = 'Hello';

  changeFn(e:any) {
    this.value = e.target.value;
  }
  modelChangeFn(value:any) {
    this.value = value;
  }

  addPrefix(){
    this.value="calix-"+this.value
  }


}
